import {
  Pressable,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
  Alert,
  FlatList,
} from "react-native";
import React, { useEffect, useState } from "react";
import Heading from "../components/Heading";
import { COLORS, HOST_API, SIZES } from "../constants";
import Button from "../components/Button";
import * as ImagePicker from "expo-image-picker";
import { Image } from "react-native";
import { TouchableOpacity } from "react-native";
import { KeyboardAwareScrollView } from "react-native-keyboard-aware-scroll-view";
import axios from "axios";
import { Ionicons } from "@expo/vector-icons";
import { useNavigation } from "@react-navigation/native";

const RegisterTutor = () => {
  const steps = [
    { title: "Tài khoản", content: "Info Form" },
    { title: "Thông tin", content: "Chuyen Mon" },
    { title: "Chuyên môn", content: "Chuyên môn" },
    { title: "Bằng cấp", content: "Bang Cap" },
  ];
  const [currentStep, setCurrentStep] = useState(0);
  const navigation = useNavigation();

  const [name, setName] = useState();
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const [address, setAddress] = useState();
  const [phone, setPhone] = useState();
  const [university, setUniversity] = useState();
  const [major, setMajor] = useState();

  //Tỉnh thành
  const [selectProvince, setSelectProvince] = useState(
    "Chọn tỉnh thành nơi dậy"
  );
  const [isClickProvince, setIsClickProvince] = useState(false);
  const [province, setProvince] = useState([]);
  const [loader, setLoader] = useState(false);
  useEffect(() => {
    setLoader(true);
    const fetchProvince = async () => {
      try {
        const response = await axios.get(
          HOST_API.local + `/api/district/province`
        );
        setProvince(response.data);
      } catch (error) {
        console.log("error", error);
      } finally {
        setLoader(false);
      }
    };
    fetchProvince();
  }, []);

  //Quận huyện
  const [selectDistrict, setSelectDistrict] = useState(
    "Chọn quận/huyện nơi dậy"
  );
  const [isClickDistrict, setIsClickDistrict] = useState(false);
  const [district, setDistrict] = useState([]);
  const [districtValue, setDistrictValue] = useState();
  async function handLoadDistrict(idProvince) {
    const response = await fetch(
      HOST_API.local + `/api/district/province/${idProvince}`
    );
    setDistrict(await response.json());
  }

  //Giới tính
  const [selectGender, setSelectGender] = useState("Chọn giới tính gia sư");
  const [isClickGender, setIsClickGender] = useState(false);
  const [genderValue, setGenderValue] = useState("");
  const GioiTinh = [
    { label: "Nam", value: "Nam" },
    { label: "Nữ", value: "Nữ" },
    { label: "Nam - Nữ", value: "Nam - Nữ" },
  ];

  const [imgProfile, setImgProfile] = useState();
  const [imageProfile, setImageProfile] = useState();
  const [isImage, setIsImage] = useState(false);
  const pickerImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    console.log(result);
    if (!result.canceled) {
      setIsImage(true);
      setImageProfile(result.assets[0].uri);
      setImgProfile(result.assets[0].fileName);
    }
  };

  const [imgCertificate1, setImgCertificate1] = useState(null);
  const [certificate1, setCertificate1] = useState(null);
  const pickerCeti1 = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });
    console.log(result);
    if (!result.canceled) {
      setIsImage(true);
      setCertificate1(result.assets[0].uri);
      setImgCertificate1(result.assets[0].fileName);
    }
  };

  const [imgCertificate2, setImgCertificate2] = useState(null);
  const [certificate2, setCertificate2] = useState(null);
  const pickerCeti2 = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [1, 1],
      quality: 1,
    });
    console.log(result);
    if (!result.canceled) {
      setIsImage(true);
      setCertificate2(result.assets[0].uri);
      setImgCertificate2(result.assets[0].fileName);
    }
  };

  const [checkEmail, setCheckEmail] = useState(false);
  const handleCheckEmail = async (email) => {
    console.log(email);
    await axios
      .get(HOST_API.local + `/api/auth/emailExist/${email}`)
      .then((response) => {
        console.log(response.data);
        setCheckEmail(response.data);
        if (checkEmail.data == true) {
          Alert.alert("Email đã được đăng kí", "Nhập lại email", [
            {
              text: "Cancel",
              onPress: () => {},
            },
            { defaultIndex: 1 },
          ]);
          setEmail("");
        } else {
          setCurrentStep(1);
        }
      });
  };

  const register = async () => {
    const user = {
      email: email,
      idNumber: 1,
      password: password,
      fullname: name,
      phone: phone,
      address: address,
      districtId: districtValue,
      gender: genderValue,
      university: university,
      major: major,
      area: selectDistrict + ", " + selectProvince,
      imgCertificate: imgCertificate1,
      imgAvatar: imgProfile,
      imgIdFront: imgCertificate1,
      imdIdBack: imgCertificate2,
    };
    console.log("Value: ", user);

    setLoader(true);
    try {
      const endpoint = HOST_API.local + "/api/auth/registerTutor";
      const response = await axios.post(endpoint, {
        email: user.email,
        idNumber: 1,
        password: user.password,
        fullname: user.name,
        phone: user.phone,
        address: user.address,
        districtId: user.districtValue,
        gender: user.genderValue,
        university: user.university,
        major: user.major,
        area: user.area,
        imgCertificate: user.certificate1,
        imgAvatar: user.imageProfile,
        imgIdFront: user.certificate2,
        imdIdBack: user.certificate2,

        // email: "DemoRegisterTutor@gmail.com",
        // idNumber: 1,
        // password: "1",
        // fullname: "Demo Register Tutor",
        // phone: "0838228607",
        // address: "123 Demo",
        // districtId: 2,
        // gender: "Nam",
        // university: "FPT University",
        // major: "Công Nghệ Thông Tin",
        // area: "Quận Hoàn Kiếm, Thành phố Hà Nội",
        // imgCertificate: "IMG_3329.jpg",
        // imgAvatar: "IMG_3556.jpg",
        // imgIdFront: "IMG_3329.jpg",
        // imdIdBack: "IMG_3198.JPG",
      });
      console.log(response.data);
      if (response.data.responseCode === "00") {
        console.log(response.data);
        Alert.alert("Chúc mừng ", "Đăng kí tài khoản thành công", [
          {
            text: "Cancel",
            onPress: () => {},
          },
          {
            text: "Continue",
            onPress: () => {
              navigation.replace("Login");
            },
          },
          { defaultIndex: 1 },
        ]);
        setLoader(false);
      } else {
        Alert.alert("Error Logging im", "Please provide all require fields", [
          {
            text: "Cancel",
            onPress: () => {},
          },
          {
            text: "Continue",
            onPress: () => {},
          },
          { defaultIndex: 1 },
        ]);
      }
    } catch (error) {
      console.log(error.message);
      Alert.alert("Error", "error", [
        {
          text: "Cancel",
          onPress: () => {},
        },
        {
          text: "Continue",
          onPress: () => {},
        },
        { defaultIndex: 1 },
      ]);
    } finally {
      setLoader(false);
    }
  };

  return (
    <ScrollView style={{ marginTop: 30, marginBottom: 20 }}>
      <Heading title={"Đăng kí gia sư"} />
      <View style={{ flex: 1, paddingHorizontal: 20, paddingTop: 30 }}>
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            marginBottom: 20,
            justifyContent: "space-between",
          }}
        >
          {steps?.map((step, index) => (
            <View style={{ justifyContent: "center", alignItems: "center" }}>
              {index > 0 && (
                <View
                  style={[
                    { flex: 1, height: 2, backgroundColor: "green" },
                    index <= currentStep && { backgroundColor: "green" },
                  ]}
                />
              )}
              <View
                style={[
                  {
                    width: 30,
                    height: 30,
                    borderRadius: 15,
                    backgroundColor: "#ccc",
                    justifyContent: "center",
                    alignItems: "center",
                  },
                  index < currentStep && { backgroundColor: "green" },
                ]}
              >
                {index < currentStep ? (
                  <Text
                    style={{ fontSize: 16, fontWeight: "bold", color: "white" }}
                  >
                    &#10003;
                  </Text>
                ) : (
                  <Text
                    style={{ fontSize: 16, fontWeight: "bold", color: "white" }}
                  >
                    {index + 1}
                  </Text>
                )}
              </View>
              <Text style={{ textAlign: "center", marginTop: 8 }}>
                {step.title}
              </Text>
            </View>
          ))}
        </View>
      </View>

      {currentStep == 0 && (
        <View style={{ marginHorizontal: 20 }}>
          <Text style={{ fontSize: 16, fontWeight: "bold" }}>
            Thông tin tài khoản
          </Text>
          <Pressable>
            <View>
              <Text style={styles.itemText}>Email </Text>
              <TextInput
                keyboardType="email-address"
                style={styles.input}
                value={email}
                onChangeText={(text) => setEmail(text)}
                placeholder="Nhập email"
              />
            </View>
            <View>
              <Text style={styles.itemText}>Password </Text>
              <TextInput
                style={styles.input}
                value={password}
                onChangeText={(text) => setPassword(text)}
                placeholder="Nhập password"
              />
            </View>

            <View>
              <Text style={styles.itemText}>Fullname </Text>
              <TextInput
                style={styles.input}
                value={name}
                onChangeText={(text) => setName(text)}
                placeholder="Nhập họ và tên"
              />
            </View>
          </Pressable>
          {/* {email != null && password != null && name != null && (
           
          )} */}

          <Button
            title={"Tiếp túc"}
            onPress={() => handleCheckEmail(email)}
            isValid={name}
            loader={false}
          />
        </View>
      )}

      {currentStep == 1 && (
        <View style={{ marginHorizontal: 20 }}>
          <Text style={{ fontSize: 16, fontWeight: "bold" }}>
            Thông tin cá nhân
          </Text>
          <Pressable>
            <KeyboardAwareScrollView extraScrollHeight={-50}>
              <View style={styles.avatar}>
                {isImage === false ? (
                  <View style={{ alignItems: "center" }}>
                    <Image
                      source={require("../assets/images/userDefault.png")}
                      style={styles.profileImg}
                    />
                    <TouchableOpacity
                      onPress={() => pickerImage()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Avatar</Text>
                    </TouchableOpacity>
                  </View>
                ) : (
                  <View style={{ alignItems: "center" }}>
                    {imageProfile && (
                      <Image
                        source={{ uri: imageProfile }}
                        style={styles.profileImg}
                      />
                    )}
                    <TouchableOpacity
                      onPress={() => pickerImage()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Avatar</Text>
                    </TouchableOpacity>
                  </View>
                )}
              </View>

              {/* Tỉnh thành */}
              <View>
                <Text style={styles.itemText}>Tỉnh thành</Text>
                <TouchableOpacity
                  style={styles.dropdownSelector}
                  onPress={() => {
                    setIsClickProvince(!isClickProvince);
                  }}
                >
                  <Text>{selectProvince}</Text>
                  {isClickProvince ? (
                    <Ionicons name="chevron-down-outline" size={24} />
                  ) : (
                    <Ionicons name="chevron-up-outline" size={24} />
                  )}
                </TouchableOpacity>
                {isClickProvince && (
                  <View style={styles.dropdownArea}>
                    <FlatList
                      data={province.data}
                      renderItem={({ item, index }) => {
                        return (
                          <TouchableOpacity
                            style={styles.item}
                            onPress={() => {
                              setSelectProvince(item.name);
                              setIsClickProvince(false);
                              handLoadDistrict(item.id);
                            }}
                          >
                            <Text>{item.name}</Text>
                          </TouchableOpacity>
                        );
                      }}
                    />
                  </View>
                )}
              </View>

              {/* Quận huyện */}
              <View>
                <Text style={styles.itemText}>Quận huyện</Text>
                <TouchableOpacity
                  style={styles.dropdownSelector}
                  onPress={() => {
                    setIsClickDistrict(!isClickDistrict);
                  }}
                >
                  <Text>{selectDistrict}</Text>
                  {isClickDistrict ? (
                    <Ionicons name="chevron-down-outline" size={24} />
                  ) : (
                    <Ionicons name="chevron-up-outline" size={24} />
                  )}
                </TouchableOpacity>
                {isClickDistrict && (
                  <View style={styles.dropdownArea}>
                    <FlatList
                      data={district.data}
                      renderItem={({ item, index }) => {
                        return (
                          <TouchableOpacity
                            style={styles.item}
                            onPress={() => {
                              setSelectDistrict(item.name);
                              setIsClickDistrict(false);
                              setDistrictValue(item.id);
                            }}
                          >
                            <Text>{item.name}</Text>
                          </TouchableOpacity>
                        );
                      }}
                    />
                  </View>
                )}
              </View>

              <View>
                <Text style={styles.itemText}>Địa chỉ </Text>
                <TextInput
                  style={styles.input}
                  value={address}
                  onChangeText={(text) => setAddress(text)}
                  placeholder="Nhập địa chỉ của bạn"
                />
              </View>

              {/* Giới tính */}
              <View>
                <Text style={styles.itemText}>Giới tính </Text>
                <TouchableOpacity
                  style={styles.dropdownSelector}
                  onPress={() => {
                    setIsClickGender(!isClickGender);
                  }}
                >
                  <Text>{selectGender}</Text>
                  {isClickGender ? (
                    <Ionicons name="chevron-down-outline" size={24} />
                  ) : (
                    <Ionicons name="chevron-up-outline" size={24} />
                  )}
                </TouchableOpacity>
                {isClickGender && (
                  <View style={styles.dropdownArea}>
                    <FlatList
                      data={GioiTinh}
                      renderItem={({ item, index }) => {
                        return (
                          <TouchableOpacity
                            style={styles.item}
                            onPress={() => {
                              setSelectGender(item.label);
                              setIsClickGender(false);
                              setGenderValue(item.value);
                            }}
                          >
                            <Text>{item.label}</Text>
                          </TouchableOpacity>
                        );
                      }}
                    />
                  </View>
                )}
              </View>

              <View>
                <Text style={styles.itemText}>Số điện thoại </Text>
                <TextInput
                  keyboardType="phone-pad"
                  style={styles.input}
                  value={phone}
                  onChangeText={(text) => setPhone(text)}
                  placeholder="Nhập số điện thoại"
                />
              </View>
            </KeyboardAwareScrollView>
          </Pressable>

          <Button
            title={"Trở lại"}
            onPress={() => setCurrentStep(0)}
            isValid={name}
            loader={false}
          />

          <Button
            title={"Tiếp túc"}
            onPress={() => setCurrentStep(2)}
            isValid={name}
            loader={false}
          />
          {/* {address != "" && phone != "" && address != null && phone != null && (
          
          )} */}
        </View>
      )}

      {currentStep == 2 && (
        <View style={{ marginHorizontal: 20 }}>
          <Text style={{ fontSize: 16, fontWeight: "bold" }}>
            Thông tin chuyên môn
          </Text>
          <Pressable>
            <KeyboardAwareScrollView extraScrollHeight={-150}>
              <View>
                <Text style={styles.itemText}>Trương đại học </Text>
                <TextInput
                  style={styles.input}
                  value={university}
                  onChangeText={(text) => setUniversity(text)}
                  placeholder="Nhập trương đại học"
                />
              </View>

              <View>
                <Text style={styles.itemText}>Ngành học </Text>
                <TextInput
                  style={styles.input}
                  value={major}
                  onChangeText={(text) => setMajor(text)}
                  placeholder="Nhập ngành học"
                />
              </View>

              <View>
                <Text style={styles.itemText}>Chuyên môn (Môn dạy): </Text>
                <TextInput
                  style={styles.input}
                  value={major}
                  onChangeText={(text) => setMajor(text)}
                  placeholder="Nhập họ và tên"
                />
              </View>
            </KeyboardAwareScrollView>
          </Pressable>

          <Button
            title={"Trở lại"}
            onPress={() => setCurrentStep(1)}
            isValid={name}
            loader={false}
          />
          <Button
            title={"Tiếp túc"}
            onPress={() => setCurrentStep(3)}
            isValid={name}
            loader={false}
          />
        </View>
      )}

      {currentStep == 3 && (
        <View style={{ marginHorizontal: 20 }}>
          <Text style={{ fontSize: 16, fontWeight: "bold" }}>
            Thông tin bằng cấp
          </Text>
          <Pressable>
            <KeyboardAwareScrollView extraScrollHeight={-150}>
              <View style={styles.cetification}>
                {isImage === false ? (
                  <View style={{ alignItems: "center" }}>
                    <Image
                      source={require("../assets/images/userDefault.png")}
                      style={styles.certificate}
                    />
                    <TouchableOpacity
                      onPress={() => pickerCeti1()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Bằng cấp 1</Text>
                    </TouchableOpacity>
                  </View>
                ) : (
                  <View style={{ alignItems: "center" }}>
                    {certificate1 && (
                      <Image
                        source={{ uri: certificate1 }}
                        style={styles.certificate}
                      />
                    )}
                    <TouchableOpacity
                      onPress={() => pickerCeti1()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Bằng cấp 1</Text>
                    </TouchableOpacity>
                  </View>
                )}
              </View>
              {/* certificate */}
              <View style={styles.avatar}>
                {isImage === false ? (
                  <View style={{ alignItems: "center" }}>
                    <Image
                      source={require("../assets/images/userDefault.png")}
                      style={styles.certificate}
                    />
                    <TouchableOpacity
                      onPress={() => pickerCeti2()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Bằng cấp 2</Text>
                    </TouchableOpacity>
                  </View>
                ) : (
                  <View style={{ alignItems: "center" }}>
                    {certificate1 && (
                      <Image
                        source={{ uri: certificate2 }}
                        style={styles.certificate}
                      />
                    )}
                    <TouchableOpacity
                      onPress={() => pickerCeti2()}
                      style={{
                        marginTop: 20,
                        height: 50,
                        width: "60%",
                        backgroundColor: "skyblue",
                        borderRadius: 20,
                        justifyContent: "center",
                        alignItems: "center",
                        alignSelf: "center",
                      }}
                    >
                      <Text style={styles.itemText}>Bằng cấp 2</Text>
                    </TouchableOpacity>
                  </View>
                )}
              </View>
            </KeyboardAwareScrollView>
          </Pressable>

          <Button
            title={"Trở lại"}
            onPress={() => setCurrentStep(2)}
            isValid={name}
            loader={false}
          />
          <Button
            title={"Đăng kí"}
            onPress={() => register()}
            isValid={name}
            loader={false}
          />
        </View>
      )}
    </ScrollView>
  );
};

