      parent: profileId,
      address: address,
      major: subjectValue,
      classNo: classValue,
      slot: slot,
      level: levelValue,
      gender: genderValue,
      price: price,
      dateStart: dateStart,
      dateEnd: dateEnd,
      academicAbility: academicValue,
      description: description,
      status: "Chưa duyệt",
