        </View>
        <View style={{ zIndex: 18 }}>
          <Text style={styles.itemText}>Trình độ</Text>
          <DropDownPicker
            items={trinhDo}
            open={levelOpen}
            setOpen={() => setLevelOpen(!levelOpen)}
