              </TouchableOpacity>
            </View>
          )}
          {!showPicker && (
            <Pressable onPress={toggleDatePickerStart}>
              <TextInput
                style={styles.input}
                value={dateStart}
                onChangeText={setDateStart}
                placeholder="Ngày bắt đầu "
                editable={false}
                onPressIn={toggleDatePickerStart}
              />
            </Pressable>
          )}
        </View>

        <View>
          <Text style={styles.itemText}>Ngày kết thúc</Text>
          {showPickerEnd && (
            <DateTimePicker
              mode="date"
              display="spinner"
              value={date}
              onChange={onChangeEnd}
              style={styles.datePicker}
              minimumDate={new Date("2020-1-1")}
            />
          )}

          {showPickerEnd && Platform.OS === "ios" && (
            <View
              style={{ flexDirection: "row", justifyContent: "space-around" }}
            >
              <TouchableOpacity
                style={[
                  styles.button,
                  styles.picketButton,
                  { backgroundColor: "#11182711" },
                ]}
                onPress={toggleDatePickerEnd}
              >
                <Text style={[styles.buttonText, { color: "#075985" }]}>
                  Cancel
                </Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.button, styles.picketButton]}
                onPress={confirmDateEnd}
              >
                <Text style={[styles.buttonText]}>Confirm</Text>
              </TouchableOpacity>
            </View>
          )}
          {!showPickerEnd && (
            <Pressable onPress={toggleDatePickerEnd}>
              <TextInput
                style={styles.input}
                value={dateEnd}
                onChangeText={setDateEnd}
                placeholder="Ngày kết thúc "
                editable={false}
                onPressIn={toggleDatePickerEnd}
              />
            </Pressable>
          )}
        </View>

        <View>
          <Text style={styles.itemText}>Mô tả thêm </Text>
          <TextInput
            style={styles.inputArea}
            value={description}
            multiline
            numberOfLines={10}
            onChangeText={(text) => setDescription(text)}
            placeholder="Thông tin thêm"
          />
        </View>
        <TouchableOpacity onPress={handleCreate}>
          <View style={styles.btn}>
            <Text style={styles.btnText}>Đăng kí</Text>
          </View>
        </TouchableOpacity>
      </KeyboardAwareScrollView>
    </View>
  );
};

