import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React from "react";
import { useNavigation, useRoute } from "@react-navigation/native";
import Heading from "../components/Heading";
import { COLORS, HOST_API, SIZES } from "../constants";
import { useState } from "react";
import { Alert } from "react-native";
import { Ionicons, Feather } from "@expo/vector-icons";
import { KeyboardAwareScrollView } from "react-native-keyboard-aware-scroll-view";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

const FeedbackClass = () => {
  const navigation = useNavigation();

  const route = useRoute();
  const { item, length, user } = route.params;
  console.log(item);
  const { checkEndDate, setCheckEndDate } = useState(false);
  const [content, setContent] = useState("");
  const [startRating, setStartRating] = useState(null);
  const [maxRating, setMaxRating] = useState([1, 2, 3, 4, 5]);

  var date = new Date();
  var month = new Date().getMonth() + 1;
  var day = date.getDate() + "/" + month + "/" + date.getFullYear();
  if (day === item.dateEnd) {
    setCheckEndDate(true);
  }

  const createFeedback = async () => {
    const token = await AsyncStorage.getItem("token");
    console.log(token);
    const feedback = {
      clazzId: item.id,
      tutorId: item.tutor.id,
      rating: startRating,
      content: content,
    };
    console.log(feedback);
    axios
      .post(
        HOST_API.local + `/api/feedback/create`,
        {
          clazzId: feedback.clazzId,
          tutorId: feedback.tutorId,
          rating: feedback.rating,
          content: feedback.content,
        },
        {
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      )
      .then((response) => {
        console.log(response.data);
        Alert.alert("Tạo điểm danh thành công", "Quản lý lớp", [
          {
            text: "Cancel",
            onPress: () => navigation.navigate("ManageClass", { profileId }),
          },
          {
            text: "Continue",
            onPress: () => {
              navigation.navigate("ManageClass", { profileId });
            },
          },
          { defaultIndex: 1 },
        ]);
      })
      .catch((error) => {
        Alert.alert("Tạo điểm danh không thành công", "Quản lý lớp", [
          {
            text: "Cancel",
            onPress: () => {},
          },
          {
            text: "Continue",
            onPress: () => {
              navigation.navigate("ManageClass", { profileId });
            },
          },
          { defaultIndex: 1 },
        ]);
        console.log("Create failed", error);
      });
  };

  const handleCheckout = () => {
    Alert.alert("Bạn có muốn gửi đánh giá ", " ", [
      {
        text: "Cancel",
        onPress: () => {},
      },
      {
        text: "Continue",
        onPress: () => {
          createFeedback();
          // navigation.navigate("Profile");
        },
      },
      { defaultIndex: 1 },
    ]);
  };
  const formattedAmount = new Intl.NumberFormat("vi-VN", {
    style: "currency",
    currency: "VND",
  }).format(item.tuition);

  const majors = ({ item }) => {
    var major = "";
    var classNo = "";
    for (let index = 0; index < item.subjects.length; index++) {
      if (index == item.subjects.length - 1) {
        major += item.subjects[index].name;
      } else {
        major += item.subjects[index].name + ", ";
      }
      classNo = item.subjects[index].level;
    }
    return { major, classNo };
  };

  return (
    <SafeAreaView>
      <Heading title={"Đánh giá chất lượng"} />
      <View style={styles.class}>
        <View style={styles.classHeading}>
          <View style={styles.headingName}>
            <Text style={styles.name}>{majors({ item }).major}</Text>
          </View>
        </View>
        <View style={styles.info}>
          <View style={styles.infoDetail}>
            <Text style={styles.sup}>Môn học: {majors({ item }).classNo}</Text>
            <Text style={styles.sup}>{majors({ item }).classNo}</Text>
            <Text style={styles.sup}>Ngày bắt đầu: {item.dateStart}</Text>
            <Text style={styles.sup}>Ngày kết thúc: {item.dateEnd}</Text>
            <Text style={styles.sup}>Gia sư: {item.tutorLevel}</Text>
            <Text style={styles.sup}>{formattedAmount}</Text>
          </View>
        </View>

        <View
          style={{
            justifyContent: "center",
            alignItems: "center",
            marginTop: 10,
            marginBottom: -10,
            marginHorizontal: 10,
          }}
        >
          <View style={styles.createAttendance}>
            <Text style={styles.titleText}>
              Số buổi: {length}/ {item.slots}
            </Text>
          </View>
        </View>
        <View
          style={{
            marginHorizontal: 20,
            marginTop: 20,
            borderWidth: 1,
            borderColor: COLORS.gray,
          }}
        ></View>
      </View>
      <View style={{ marginHorizontal: 20, marginTop: 10 }}>
        <View>
          <View
            style={{
              justifyContent: "center",
              alignItems: "center",
              flexDirection: "row",
            }}
          >
            <Text>{startRating ? `${startRating}` : "Tap to rate"}</Text>
            <TouchableOpacity onPress={() => setStartRating(1)}>
              <Ionicons
                name={startRating >= 1 ? "star" : "star-outline"}
                size={35}
                style={
                  startRating >= 1 ? styles.startSelect : styles.startUnSelect
                }
              />
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setStartRating(2)}>
              <Ionicons
                name={startRating >= 2 ? "star" : "star-outline"}
                size={35}
                style={
                  startRating >= 2 ? styles.startSelect : styles.startUnSelect
                }
              />
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setStartRating(3)}>
              <Ionicons
                name={startRating >= 3 ? "star" : "star-outline"}
                size={35}
                style={
                  startRating >= 3 ? styles.startSelect : styles.startUnSelect
                }
              />
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setStartRating(4)}>
              <Ionicons
                name={startRating >= 4 ? "star" : "star-outline"}
                size={35}
                style={
                  startRating >= 4 ? styles.startSelect : styles.startUnSelect
                }
              />
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setStartRating(5)}>
              <Ionicons
                name={startRating >= 5 ? "star" : "star-outline"}
                size={35}
                style={
                  startRating >= 5 ? styles.startSelect : styles.startUnSelect
                }
              />
            </TouchableOpacity>
          </View>
          <KeyboardAwareScrollView extraScrollHeight={20}>
            <Text style={styles.itemText}>Đánh giá chất lượng gia sư </Text>
            <TextInput
              style={styles.inputArea}
              value={content}
              multiline
              numberOfLines={10}
              onChangeText={(text) => setContent(text)}
              placeholder="Thông tin thêm"
            />
          </KeyboardAwareScrollView>
        </View>
        {length == item.slots ? (
          <TouchableOpacity onPress={handleCheckout}>
            <View style={styles.btn}>
              <Text style={styles.btnText}>
                Lớp học hoàn thành - Kết thúc lớp học
              </Text>
            </View>
          </TouchableOpacity>
        ) : checkEndDate ? (
          <TouchableOpacity onPress={handleCheckout}>
            <View style={styles.btn}>
              <Text style={styles.btnText}>
                Chưa hoàn hành - Đánh giá chất lượng
              </Text>
            </View>
          </TouchableOpacity>
        ) : (
          <TouchableOpacity onPress={handleCheckout}>
            <View style={styles.btn}>
              <Text style={styles.btnText}>Yêu cầu hủy - Kết thúc lớp học</Text>
            </View>
          </TouchableOpacity>
        )}
      </View>
    </SafeAreaView>
  );
};

export default FeedbackClass;

const styles = StyleSheet.create({
  headingName: {
    marginTop: -10,
    width: 300,
    borderColor: COLORS.main,
    borderRadius: 20,
    borderWidth: 2,
    alignItems: "center",
    backgroundColor: COLORS.main,
  },

  classHeading: {
    alignItems: "center",
    marginTop: 40,
    zIndex: 999,
  },
  name: {
    fontFamily: "bold",
    fontSize: SIZES.xLarge,
    color: COLORS.lightWhite,
  },
  info: {
    borderWidth: 2,
    borderColor: COLORS.black,
    borderRadius: 30,
    marginHorizontal: 20,
    marginTop: -10,
    backgroundColor: COLORS.secondMain,
  },
  infoDetail: {
    marginLeft: 20,
    marginTop: 10,
  },
  sup: {
    fontFamily: "regular",
    fontSize: SIZES.large,
    color: COLORS.black,
    marginBottom: 5,
  },
  titleText: {
    fontFamily: "bold",
    fontSize: SIZES.medium,
    color: COLORS.black,
  },
  createAttendance: {
    padding: 5,
    flexDirection: "row",
    justifyContent: "space-between",
    borderColor: COLORS.black,
    borderWidth: 2,
    backgroundColor: COLORS.secondMain,
    borderRadius: 20,
  },
  itemText: {
    padding: 10,
    fontFamily: "bold",
    fontSize: SIZES.medium,
    color: COLORS.main,
  },
  inputArea: {
    marginHorizontal: 10,
    paddingHorizontal: 10,
    paddingVertical: 15,
    height: 90,
    borderWidth: 1,
    borderRadius: 10,
    backgroundColor: COLORS.lightWhite,
  },
  btnText: {
    borderWidth: 2,
    borderColor: COLORS.main,
    padding: 15,
    borderRadius: 15,
    fontFamily: "regular",
    fontWeight: "bold",
    fontSize: SIZES.medium,
    color: COLORS.main,
  },
  btn: {
    marginTop: 10,
    marginBottom: 20,
    justifyContent: "center",
    alignItems: "center",
  },

  startSelect: {
    color: "#FFCE0A",
    marginHorizontal: 5,
  },
  startUnSelect: {
    color: "black",
    marginHorizontal: 5,
  },
});
