import {
  TouchableOpacity,
  StyleSheet,
  Text,
  View,
  ScrollView,
  Image,
  Alert,
  ActivityIndicator,
} from "react-native";
import React, { useState, useEffect } from "react";
import { SafeAreaView } from "react-native-safe-area-context";
import {
  Ionicons,
  MaterialCommunityIcons,
  SimpleLineIcons,
  AntDesign,
} from "@expo/vector-icons";
import { COLORS, HOST_API, SIZES } from "../constants";
import { useNavigation } from "@react-navigation/native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

const Profile = () => {
  const navigation = useNavigation();

  const [loader, setLoader] = useState(false);

  const [userData, setUserData] = useState(null);
  const [user, setUser] = useState(null);
  const [userLogin, setUserLogin] = useState(false);
  useEffect(() => {
    checkExitingUser();
  }, []);

  const checkExitingUser = async () => {
    const token = await AsyncStorage.getItem("token");
    if (token != null) {
      setLoader(true);
      try {
        const currentUser = await axios.get(
          HOST_API.local + `/api/user/authProfile`,
          {
            headers: {
              Authorization: "Bearer " + token,
            },
          }
        );
        if (currentUser !== null) {
          setUserData(currentUser.data.data);
          setUserLogin(true);
          setUser(currentUser.data.data.id);
        }
      } catch (error) {
        console.log("error", error);
      } finally {
        setLoader(false);
      }
    } else {
      setLoader(false);
    }
  };

  const userLogout = async () => {
    const id = await AsyncStorage.getItem("token");
    try {
      await AsyncStorage.removeItem("token");
      navigation.replace("Bottom Navigation");
    } catch (error) {
      console.log("Error retrieving the data: ", error);
    }
  };

  const logout = () => {
    Alert.alert("logout", "Are you sure you want to logout", [
      {
        text: "Cancel",
        onPress: () => console.log("cancel pressed"),
      },
      {
        text: "Continue",
        onPress: () => userLogout(),
      },
    ]);
  };

  return (
    <SafeAreaView>
      <View style={styles.heading}>
        <Text style={styles.headingText}> Thong tin ca nhan</Text>
        <View style={{ alignItems: "flex-end", marginRight: 10 }}>
          <View style={styles.cartCount}>
            <Text style={styles.cartNumber}>8</Text>
          </View>
          <TouchableOpacity>
            <Ionicons name="notifications-circle" size={45} />
          </TouchableOpacity>
        </View>
      </View>
      {loader ? (
        <ActivityIndicator size={500} color={COLORS.main} />
      ) : (
        <View style={styles.profilePage}>
          <View style={styles.profile}>
            <View style={styles.edit}>
              <Text></Text>
              {userLogin === true ? (
                <TouchableOpacity
                  onPressIn={() => navigation.navigate("EditProfile")}
                >
                  <Ionicons
                    name="create-outline"
                    size={40}
                    color={COLORS.main}
                  />
                </TouchableOpacity>
              ) : (
                <Text style={{ marginBottom: 30 }}></Text>
              )}
            </View>
            <View style={styles.info}>
              <Image
                source={require("../assets/images/profile.jpeg")}
                style={styles.profileImg}
              />
              <Text style={styles.name}>
                {userLogin === true
                  ? userData.fullName
                  : "Please login into your account"}
              </Text>
              {userLogin === false ? (
                <TouchableOpacity
                  onPressIn={() => navigation.navigate("Login")}
                >
                  <View style={styles.loginBtn}>
                    <Text style={styles.menuText}>Đăng nhập </Text>
                  </View>
                </TouchableOpacity>
              ) : (
                <View style={styles.loginBtn}>
                  <Text style={styles.email}>{userData.fullName}</Text>
                  <Text style={styles.supplier}>{userData.role}</Text>
                </View>
              )}
              {userLogin === false ? (
                <View></View>
              ) : (
                <View style={styles.menuWrapper}>
                  <TouchableOpacity
                    onPressIn={() =>
                      navigation.navigate("Wallet", { userData })
                    }
                  >
                    <View style={styles.menuItem(0.5)}>
                      <MaterialCommunityIcons
                        name="wallet-outline"
                        size={30}
                        color={COLORS.main}
                      />
                      <Text style={styles.menuItemText}> Ví của bạn </Text>
                    </View>
                  </TouchableOpacity>
                  {userData.role === "TUTOR" ? (
                    <TouchableOpacity
                      onPressIn={() =>
                        navigation.navigate("ManageApply", { user })
                      }
                    >
                      <View style={styles.menuItem(0.5)}>
                        <MaterialCommunityIcons
                          name="frequently-asked-questions"
                          size={30}
                          color={COLORS.main}
                        />
                        <Text style={styles.menuItemText}>
                          Danh sách lớp apply
                        </Text>
                      </View>
                    </TouchableOpacity>
                  ) : (
                    <TouchableOpacity
                      onPressIn={() =>
                        navigation.navigate("ManageRequest", {
                          user,
                          userData,
                        })
                      }
                    >
                      <View style={styles.menuItem(0.5)}>
                        <MaterialCommunityIcons
                          name="frequently-asked-questions"
                          size={30}
                          color={COLORS.main}
                        />
                        <Text style={styles.menuItemText}>
                          Quản lý yêu cầu tạo lớp
                        </Text>
                      </View>
                    </TouchableOpacity>
                  )}

                  <TouchableOpacity
                    onPressIn={() =>
                      navigation.navigate("ManageClass", { user })
                    }
                  >
                    <View style={styles.menuItem(0.5)}>
                      <MaterialCommunityIcons
                        name="clipboard-text-clock"
                        size={30}
                        color={COLORS.main}
                      />
                      <Text style={styles.menuItemText}>
                        Quản lý lớp & Điêm danh
                      </Text>
                    </View>
                  </TouchableOpacity>

                  <TouchableOpacity>
                    <View style={styles.menuItem(0.5)}>
                      <AntDesign
                        name="deleteuser"
                        size={30}
                        color={COLORS.main}
                      />
                      <Text style={styles.menuItemText}> Xóa tài khoản </Text>
                    </View>
                  </TouchableOpacity>

                  <TouchableOpacity onPress={() => logout()}>
                    <View style={styles.menuItem(0.5)}>
                      <AntDesign name="logout" size={30} color={COLORS.main} />
                      <Text style={styles.menuItemText}> Đăng xuất </Text>
                    </View>
                  </TouchableOpacity>
                </View>
              )}
            </View>
          </View>
        </View>
      )}
    </SafeAreaView>
  );
};

export default Profile;

const styles = StyleSheet.create({
  menuItemText: {
    fontFamily: "regular",
    fontWeight: "600",
    fontSize: 17,
    lineHeight: 26,
    marginLeft: 20,
  },
  menuWrapper: {
    marginTop: SIZES.xxLarge,
    width: SIZES.width - SIZES.large,
    backgroundColor: COLORS.lightWhite,
    borderRadius: 12,
  },

  menuItem: (borderWidth) => ({
    borderBottomWidth: borderWidth,
    flexDirection: "row",
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderColor: COLORS.gray,
  }),

  menuText: {
    fontFamily: "regular",
    fontWeight: "600",
    fontSize: 17,
    lineHeight: 26,
  },
  email: {
    fontFamily: "bold",
  },

  logoutBtn: {
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: COLORS.secondMain,
    padding: 10,
    borderWidth: 2,
    borderColor: COLORS.primary,
    borderRadius: SIZES.small + 2,
  },

  loginBtn: {
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: COLORS.secondMain,
    padding: 10,
    borderWidth: 2,
    borderColor: COLORS.primary,
    borderRadius: SIZES.small + 2,
  },

  name: {
    fontFamily: "bold",
    fontSize: SIZES.medium + 3,
    color: COLORS.primary,
    marginVertical: 5,
  },

  info: {
    flex: 1,
    alignItems: "center",
  },

  profileImg: {
    height: 155,
    width: 155,
    borderRadius: 999,
    borderColor: COLORS.primary,
    borderWidth: 2,
    resizeMode: "cover",
    marginTop: -20,
  },

  btn: {
    borderRadius: 10,
    marginHorizontal: 10,
    justifyContent: "center",
    alignItems: "center",
    width: 150,
    backgroundColor: COLORS.main,
  },
  historyBtn: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  edit: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginHorizontal: 10,
  },

  numberText: {
    fontFamily: "bold",
    fontSize: SIZES.large,
    color: COLORS.main,
  },
  titleText: {
    marginLeft: 10,
    fontFamily: "bold",
    fontSize: SIZES.large,
  },

  title: {
    marginTop: 10,
  },

  classList: {
    alignItems: "center",
    justifyContent: "flex-start",
    marginTop: 30,
    borderColor: COLORS.main,
    borderRadius: 30,
    borderWidth: 2,
    width: 400,
    height: 500,
  },

  wallet: {
    marginTop: 30,
    borderColor: COLORS.main,
    borderRadius: 10,
    borderWidth: 2,
    width: 400,
    height: 120,
  },
  profile: {
    marginTop: 30,
    borderColor: COLORS.main,
    borderRadius: 30,
    borderWidth: 2,
    width: 350,
    height: 300,
  },
  cartCount: {
    position: "absolute",
    bottom: 25,
    width: 16,
    heightL: 30,
    borderRadius: 8,
    alignItems: "center",
    backgroundColor: "red",
    justifyContent: "center",
    zIndex: 999,
  },

  cartNumber: {
    fontFamily: "regular",
    fontWeight: "600",
    fontSize: 10,
    color: COLORS.lightWhite,
  },
  heading: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 15,
    backgroundColor: COLORS.lightWhite,
    borderRadius: 999,
    alignItems: "center",
  },

  headingText: {
    marginLeft: 10,
    fontSize: SIZES.xLarge,
    fontFamily: "bold",
    color: COLORS.main,
  },

  profilePage: {
    justifyContent: "center",
    alignItems: "center",
  },
});
