import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React from "react";
import { SafeAreaView } from "react-native";
import { useNavigation, useRoute } from "@react-navigation/native";
import { Ionicons } from "@expo/vector-icons";
import Heading from "../components/Heading";
import { COLORS, HOST_API, SIZES } from "../constants";
import AttendanceItem from "../components/attendance/AttendanceItem";
import { FlatList } from "react-native";
import { ScrollView } from "react-native";
import axios from "axios";
import { Alert } from "react-native";
import { useState } from "react";
import { RefreshControl } from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useEffect } from "react";
import { ActivityIndicator } from "react-native";

const AttendancePage = () => {
  const navigation = useNavigation();
  const [length, setLength] = useState(0);
  const [loader, setLoader] = useState(false);
  const [data, setData] = useState([]);
  const [refreshing, setRefreshing] = useState(false);
  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    setTimeout(() => {
      setRefreshing(false);
    }, 2000);
  }, []);
  const route = useRoute();
  const { item, user } = route.params;
  console.log(item);
  let temp = 1;
  useEffect(() => {
    fetchListApply();
  }, []);
  const fetchListApply = async () => {
    const token = await AsyncStorage.getItem("token");

    setLoader(true);
    try {
      const response = await axios.get(
        HOST_API.local + `/api/attendance/clazz/${item.id}`,
        {
          headers: {
            Authorization: "Bearer " + token,
          },
        }
      );
      setData(response.data);
      setLength(response.data.data.length);
    } catch (error) {
      console.log("error", error);
    } finally {
      setLoader(false);
    }
  };

  if (length == item.slots) {
    console.log("Thúc");
  }
  const majors = ({ item }) => {
    var major = "";
    var classNo = "";
    for (let index = 0; index < item.subjects.length; index++) {
      if (index == item.subjects.length - 1) {
        major += item.subjects[index].name;
      } else {
        major += item.subjects[index].name + ", ";
      }
      classNo = item.subjects[index].level;
    }
    return { major, classNo };
  };
  var date = new Date();
  var month = new Date().getMonth() + 1;
  const createAttendance = (attendance) => {
    axios
      .post(
        HOST_API.local + `/api/attendance/create?clazzId=${item.id}&status=1`
      )
      .then((response) => {
        console.log(response.data);
        Alert.alert("Tạo điểm danh thành công", "Quản lý lớp", [
          {
            text: "Cancel",
            onPress: () => {
              {
              }
            },
          },
          {
            text: "Continue",
            onPress: () => {
              navigation.navigate("ManageClass", { user });
            },
          },
          { defaultIndex: 1 },
        ]);
      })
      .catch((error) => {
        Alert.alert("Tạo điểm danh không thành công", "Quản lý lớp", [
          {
            text: "Cancel",
            onPress: () => {},
          },
          {
            text: "Continue",
            onPress: () => {
              navigation.replace("ManageClass", { user });
            },
          },
          { defaultIndex: 1 },
        ]);
        console.log("Create failed", error);
      });
  };

  return (
    <SafeAreaView style={{ marginTop: -20 }}>
      <Heading title={"Điểm danh"} />
      <View style={styles.class}>
        <View style={styles.classHeading}>
          <View style={styles.headingName}>
            <Text style={styles.name}>{majors({ item }).major}</Text>
          </View>
        </View>
        <View style={styles.info}>
          <View style={styles.infoDetail}>
            <Text style={styles.sup}>Môn học: {majors({ item }).major}</Text>
            <Text style={styles.sup}>{majors({ item }).classNo}</Text>
            <Text style={styles.sup}>Ngày bắt đầu: {item.dateStart}</Text>
            <Text style={styles.sup}>Ngày kết thúc: {item.dateEnd}</Text>
            <Text style={styles.sup}>Gia sư: {item.tutor.name}</Text>
          </View>
        </View>

        <View
          style={{
            justifyContent: "space-between",
            flexDirection: "row",
            alignItems: "center",
            marginTop: 10,
            marginBottom: -10,
            marginHorizontal: 10,
          }}
        >
          {loader ? (
            <ActivityIndicator size={500} color={COLORS.main} />
          ) : (
            <TouchableOpacity style={styles.createAttendance}>
              <Text style={styles.titleText}>
                Số buổi: {length}/{item.slots}
              </Text>
            </TouchableOpacity>
          )}

          <TouchableOpacity
            style={styles.createAttendance}
            onPressIn={() =>
              navigation.navigate("FeedbackClass", { item, length, user })
            }
          >
            <Ionicons name="add-circle" size={20} color={COLORS.main} />
            <Text style={styles.titleText}>Đánh giá chất lượng </Text>
          </TouchableOpacity>
        </View>
        <View
          style={{
            marginHorizontal: 20,
            marginTop: 20,
            borderWidth: 1,
            borderColor: COLORS.gray,
          }}
        ></View>
      </View>
      <View style={styles.attendance}>
        <View style={styles.title}>
          <Text style={styles.titleText}>Danh sách điểm danh</Text>

          {/* <TouchableOpacity
            style={styles.createAttendance}
            onPress={createAttendance}
          >
            <Ionicons name="add-circle" size={20} color={COLORS.main} />
            <Text style={styles.titleText}>Tạo điểm danh</Text>
          </TouchableOpacity> */}
          {length == item.slots ? (
            <TouchableOpacity
              style={styles.createAttendance}
              onPressIn={() =>
                navigation.navigate("FeedbackClass", { item, length })
              }
            >
              <Ionicons name="add-circle" size={20} color={COLORS.main} />
              <Text style={styles.titleText}>Kết thúc lớp học</Text>
            </TouchableOpacity>
          ) : (
            <TouchableOpacity
              style={styles.createAttendance}
              onPress={createAttendance}
            >
              <Ionicons name="add-circle" size={20} color={COLORS.main} />
              <Text style={styles.titleText}>Tạo điểm danh</Text>
            </TouchableOpacity>
          )}
        </View>
      </View>
      <View style={{ marginTop: 10 }}>
        <FlatList
          refreshing={refreshing}
          onRefresh={onRefresh}
          data={data.data}
          style={{ marginBottom: 750 }}
          renderItem={({ item, stt = temp++ }) => (
            <AttendanceItem item={item} stt={stt} />
          )}
          keyExtractor={(item) => item.id}
        />
      </View>

      <View
        style={{
          justifyContent: "center",
          alignItems: "center",
          marginTop: 40,
        }}
      >
        <TouchableOpacity
          style={styles.createAttendance}
          onPressIn={() => navigation.navigate("FeedbackClass", { item })}
        >
          <Ionicons name="add-circle" size={20} color={COLORS.main} />
          <Text style={styles.titleText}>Kết thúc lớp học</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
};

export default AttendancePage;

const styles = StyleSheet.create({
  //Attendance
  createAttendance: {
    padding: 5,
    flexDirection: "row",
    justifyContent: "space-between",
    borderColor: COLORS.black,
    borderWidth: 2,
    backgroundColor: COLORS.secondMain,
    borderRadius: 20,
  },

  titleText: {
    fontFamily: "bold",
    fontSize: SIZES.medium,
    color: COLORS.black,
  },

  title: {
    marginTop: 10,
    flexDirection: "row",
    justifyContent: "space-between",
    marginHorizontal: 15,
  },
  // Info class
  classHeading: {
    alignItems: "center",
    marginTop: 40,
    zIndex: 999,
  },

  sup: {
    fontFamily: "regular",
    fontSize: SIZES.large,
    color: COLORS.black,
    marginBottom: 5,
  },

  infoDetail: {
    marginLeft: 20,
    marginTop: 10,
  },
  name: {
    fontFamily: "bold",
    fontSize: SIZES.xLarge,
    color: COLORS.lightWhite,
  },
  headingName: {
    marginTop: -10,
    width: 300,
    borderColor: COLORS.main,
    borderRadius: 20,
    borderWidth: 2,
    alignItems: "center",
    backgroundColor: COLORS.main,
  },

  info: {
    borderWidth: 2,
    borderColor: COLORS.black,
    borderRadius: 30,
    marginHorizontal: 20,
    marginTop: -10,
    backgroundColor: COLORS.secondMain,
  },
});
